window.docx = {
  parseDocx: async function(arrayBuffer){
    const lib = await import('https://cdn.jsdelivr.net/npm/mammoth@1.6.0/mammoth.browser.min.js');
    const res = await lib.convertToHtml({arrayBuffer});
    const tmp = document.createElement('div'); tmp.innerHTML = res.value;
    return { text: tmp.innerText };
  }
};